import React, { useState, useEffect } from "react";
import CreateUser from "../User/CreateUser";
import GetUser from "../User/GetUser";
import UpdateUser from "../User/UpdateUser";
import DeleteUser from "../User/DeleteUser";

const List = () => {
  const [users, setUsers] = useState([]);

  const refreshUsers = async () => {
    const response = await fetch("https://672a33bd976a834dd0228aa9.mockapi.io/api/vi/students");
    const data = await response.json();
    setUsers(data);
  };

  useEffect(() => {
    refreshUsers(); // 컴포넌트가 마운트될 때 사용자 데이터 불러오기
  }, []);

  return (
    <div className="container">
      <h2>CRUD Program</h2>
      <CreateUser refreshUsers={refreshUsers} />
      <UpdateUser refreshUsers={refreshUsers} />
      <DeleteUser refreshUsers={refreshUsers} />
      <GetUser users={users} setUsers={setUsers} />
    </div>
  );
};

export default List;
