import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import GetUser from "../User/GetUser"; // GetUser 컴포넌트 불러오기

const Update = () => {
  const { userId } = useParams();
  const [userData, setUserData] = useState({
    name: "",
    age: "",
    studentId: "",
    major: "",
  });
  const [editCount, setEditCount] = useState(0); // 수정 횟수 상태 추가
  const [users, setUsers] = useState([]); // 사용자 목록을 위한 상태

  useEffect(() => {
    // 페이지 로드 시 사용자 데이터 가져오기
    fetch(`https://672a33bd976a834dd0228aa9.mockapi.io/api/vi/students/${userId}`)
      .then((res) => res.json())
      .then((data) => setUserData(data));

    // 사용자 목록 가져오기
    fetch("https://672a33bd976a834dd0228aa9.mockapi.io/api/vi/students")
      .then((res) => res.json())
      .then((data) => setUsers(data));
  }, [userId]);

  const handleChange = async (e) => {
    const { name, value } = e.target;
    const updatedData = { ...userData, [name]: value };
    setUserData(updatedData);

    // 수정 횟수 증가
    setEditCount((prevCount) => prevCount + 1);

    // PUT 요청으로 수정된 값 즉시 API에 반영
    await fetch(
      `https://672a33bd976a834dd0228aa9.mockapi.io/api/vi/students/${userId}`,
      {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(updatedData),
      }
    );
  };

  return (
    <div className="container">
      <h2>Update User</h2>
      <p>Total edits made: {editCount}</p> {/* 수정 횟수 표시 */}

      <div>
        <label>Name</label>
        <input
          type="text"
          name="name"
          value={userData.name}
          onChange={handleChange}
          className="form-control mb-2"
        />
      </div>
      <div>
        <label>Age</label>
        <input
          type="number"
          name="age"
          value={userData.age}
          onChange={handleChange}
          className="form-control mb-2"
        />
      </div>
      <div>
        <label>Student ID</label>
        <input
          type="text"
          name="studentId"
          value={userData.studentId}
          onChange={handleChange}
          className="form-control mb-2"
        />
      </div>
      <div>
        <label>Major</label>
        <input
          type="text"
          name="major"
          value={userData.major}
          onChange={handleChange}
          className="form-control mb-2"
        />
      </div>

      {/* 사용자 목록을 보여주는 부분 */}
      <h3>User List</h3>
      <div>
        {users.map((user) => (
          <div key={user.id}>
            {user.id} - {user.name} ({user.age})
          </div>
        ))}
      </div>
    </div>
  );
};

export default Update;
