import React, { useState, useEffect } from "react";
import Create from "./Create"; // Create 컴포넌트 임포트

const List = () => {
  const [users, setUsers] = useState([]);

  // 사용자 데이터를 새로 고치는 함수
  const refreshUsers = async () => {
    const response = await fetch("https://672a33bd976a834dd0228aa9.mockapi.io/api/vi/students");
    const data = await response.json();
    setUsers(data);
  };

  useEffect(() => {
    refreshUsers(); // 컴포넌트가 마운트될 때 사용자 데이터 불러오기
  }, []);

  return (
    <div>
      <h3>User List</h3>
      {/* Create 컴포넌트에 refreshUsers 전달 */}
      <Create refreshUsers={refreshUsers} />
      <ul>
        {users.map((user) => (
          <li key={user.id}>{user.name} ({user.age})</li>
        ))}
      </ul>
    </div>
  );
};

export default List;
