import React, { useState, useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom"; // 페이지 이동을 위한 useNavigate 추가
import GetUser from "../User/GetUser"; // GetUser 컴포넌트 불러오기

const Update = () => {
  const [userId, setUserId] = useState(""); // 사용자 ID를 입력받기 위한 상태
  const [userData, setUserData] = useState({
    name: "",
    age: "",
    studentId: "",
    major: "",
  });
  const [editCount, setEditCount] = useState(0); // 수정 횟수 상태
  const [users, setUsers] = useState([]); // 사용자 목록을 위한 상태

  const nameRef = useRef();
  const ageRef = useRef();
  const studentIdRef = useRef();
  const majorRef = useRef();

  const navigate = useNavigate(); // 페이지 이동을 위한 useNavigate

  useEffect(() => {
    // 사용자 목록 가져오기
    fetch("https://672a33bd976a834dd0228aa9.mockapi.io/api/vi/students")
      .then((res) => res.json())
      .then((data) => setUsers(data));
  }, []);

  useEffect(() => {
    if (userId) {
      // userId가 있을 때만 해당 사용자 데이터를 가져옴
      fetch(`https://672a33bd976a834dd0228aa9.mockapi.io/api/vi/students/${userId}`)
        .then((res) => res.json())
        .then((data) => setUserData(data))
        .catch((err) => alert("User not found"));
    }
  }, [userId]); // userId가 변경될 때마다 해당 사용자 데이터를 가져옵니다.

  const handleChange = async (e) => {
    const { name, value } = e.target;
    const updatedData = { ...userData, [name]: value };
    setUserData(updatedData);

    // 수정 횟수 증가
    setEditCount((prevCount) => prevCount + 1);

    // PUT 요청으로 수정된 값 즉시 API에 반영
    await fetch(
      `https://672a33bd976a834dd0228aa9.mockapi.io/api/vi/students/${userId}`,
      {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(updatedData),
      }
    );

    // 사용자의 데이터가 수정된 후, 즉시 users 목록에 반영
    setUsers((prevUsers) =>
      prevUsers.map((user) =>
        user.id === userId ? { ...user, ...updatedData } : user
      )
    );
  };

  const handleUserIdChange = (e) => {
    setUserId(e.target.value); // userId 변경
  };

  const goToListPage = () => {
    navigate("/"); // List.js 페이지로 이동
  };

  const handleUpdate = async () => {
    // 유효성 체크
    if (userData.name === "") {
      nameRef.current.focus();
      alert("Name is required");
      return;
    }
    if (userData.age === "") {
      ageRef.current.focus();
      alert("Age is required");
      return;
    }
    if (userData.studentId === "") {
      studentIdRef.current.focus();
      alert("Student ID is required");
      return;
    }
    if (userData.major === "") {
      majorRef.current.focus();
      alert("Major is required");
      return;
    }

    await handleChange(); // 변경 사항을 저장
  };

  return (
    <div className="container">
      <h2>Update User</h2>

      <div>
        <label>User ID</label>
        <input
          type="text"
          value={userId}
          onChange={handleUserIdChange}
          className="form-control mb-2"
          placeholder="Enter User ID"
        />
      </div>

      {userId && (
        <>
          <p>Total edits made: {editCount}</p> {/* 수정 횟수 표시 */}

          <div>
            <label>Name</label>
            <input
              ref={nameRef}
              type="text"
              name="name"
              value={userData.name}
              onChange={handleChange}
              className="form-control mb-2"
            />
          </div>
          <div>
            <label>Age</label>
            <input
              ref={ageRef}
              type="number"
              name="age"
              value={userData.age}
              onChange={handleChange}
              className="form-control mb-2"
            />
          </div>
          <div>
            <label>Student ID</label>
            <input
              ref={studentIdRef}
              type="text"
              name="student_id"
              value={userData.student_id}
              onChange={handleChange}
              className="form-control mb-2"
            />
          </div>
          <div>
            <label>Major</label>
            <input
              ref={majorRef}
              type="text"
              name="major"
              value={userData.major}
              onChange={handleChange}
              className="form-control mb-2"
            />
          </div>
        </>
      )}

      <GetUser users={users} setUsers={setUsers} />

      <button className="btn btn-primary mt-3" onClick={goToListPage}>
        Go to User List
      </button>
    </div>
  );
};

export default Update;
