import React, { useState } from "react";

const Create = ({ refreshUsers }) => {
  const [name, setName] = useState("");
  const [age, setAge] = useState("");
  const [studentId, setStudentId] = useState("");  // student id 상태 추가
  const [major, setMajor] = useState("");  // major 상태 추가

  const handleCreate = async () => {
    const response = await fetch("https://672a33bd976a834dd0228aa9.mockapi.io/api/vi/students", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ name, age, studentId, major }),  // studentId와 major 추가
    });

    if (response.ok) {
      alert("등록 성공!");
      refreshUsers();
      setName("");
      setAge("");
      setStudentId("");  // studentId 초기화
      setMajor("");  // major 초기화
      // 모달 닫기
      document.getElementById("createModalClose").click();
    }
  };

  return (
    <div className="container mt-5">
      <h3>Create Data</h3>

      {/* 모달 버튼 (페이지 로드 시 모달이 열리도록 설정) */}
      <button
        type="button"
        className="btn btn-success"
        data-toggle="modal"
        data-target="#createModal"
      >
        Create User
      </button>

      {/* 모달 */}
      <div
        className="modal fade"
        id="createModal"
        tabIndex="-1"
        role="dialog"
        aria-labelledby="createModalLabel"
        aria-hidden="true"
      >
        <div className="modal-dialog" role="document">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title" id="createModalLabel">
                Create Data
              </h5>
              <button
                type="button"
                className="close"
                data-dismiss="modal"
                aria-label="Close"
                id="createModalClose"
              >
                <span aria-hidden="true">&times;</span>
              </button>
            </div>
            <div className="modal-body">
              {/* Name Input */}
              <input
                type="text"
                placeholder="Enter Name"
                className="form-control mb-2"
                value={name}
                onChange={(e) => setName(e.target.value)}
              />
              {/* Age Input */}
              <input
                type="number"
                placeholder="Enter Age"
                className="form-control mb-2"
                value={age}
                onChange={(e) => setAge(e.target.value)}
              />
              {/* Student ID Input */}
              <input
                type="text"
                placeholder="Enter Student ID"
                className="form-control mb-2"
                value={studentId}
                onChange={(e) => setStudentId(e.target.value)}
              />
              {/* Major Input */}
              <input
                type="text"
                placeholder="Enter Major"
                className="form-control mb-2"
                value={major}
                onChange={(e) => setMajor(e.target.value)}
              />
            </div>
            <div className="modal-footer">
              <button
                type="button"
                className="btn btn-secondary"
                data-dismiss="modal"
              >
                Close
              </button>
              <button
                type="button"
                className="btn btn-primary"
                onClick={handleCreate}
              >
                Save
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Create;
